import { DataSource } from '@angular/cdk/collections';
import { BehaviorSubject } from 'rxjs';
export class DataSourceUser extends DataSource {
  constructor() {
    super(...arguments);
    this.data = new BehaviorSubject([]);
    this.originalData = [];
  }

  connect() {
    return this.data;
  }

  init(data) {
    this.originalData = data;
    this.data.next(data);
  }

  disconnect() {}

}
